function addHoverListeners(e,t){e.addEventListener("mouseenter",()=>{t.forEach(e=>e.classList.add("hover"))}),e.addEventListener("mouseleave",()=>{t.forEach(e=>e.classList.remove("hover"))})}function allParticipantsHoverTogether(){for(let e=0;e<24;e++){const t=document.querySelectorAll(`div[data-participant-id="${e}"]`);for(const e of t)addHoverListeners(e,t)}}allParticipantsHoverTogether()